/* global chrome */
import { useState, useEffect } from "react";

function App() {
  const [data, setData] = useState([]);
  const [active, setActive] = useState(false);

  // âœ… On popup open, check if selecting is active
  useEffect(() => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getStatus" }, (res) => {
        if (res) setActive(res.active);
      });
    });
  }, []);

  const toggleScraper = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        { action: "toggleScraper" },
        (response) => {
          if (response) setActive(response.active);
        }
      );
    });
  };

  const getData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getData" }, (response) => {
        if (response) setData(response);
      });
    });
  };

  const clearData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "clearData" }, () => {
        setData([]);
      });
    });
  };

  const exportCSV = () => {
    if (data.length === 0) return;

    const csvContent =
      "data:text/csv;charset=utf-8," +
      ["Tag,Text", ...data.map((d) => `${d.tag},"${d.text}"`)].join("\n");

    const link = document.createElement("a");
    link.href = encodeURI(csvContent);
    link.download = "scraped_data.csv";
    link.click();
  };

  const btnStyle = (color) =>
    `px-3 py-2 rounded-lg shadow-md text-white hover:opacity-90 transition ${color}`;

  return (
    <div className="p-4 w-96 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-xl shadow-lg text-white">
      <h1 className="text-xl font-bold mb-3 text-center">âš¡ Data Scraper</h1>

      <div className="text-center mb-3">
        <span
          className={`px-2 py-1 text-xs rounded ${
            active ? "bg-green-200 text-green-900" : "bg-red-200 text-red-900"
          }`}
        >
          {active ? "ðŸŸ¢ Selecting Mode" : "ðŸ”´ Inactive"}
        </span>
      </div>

      <div className="flex flex-wrap gap-2 justify-center mb-3">
        <button onClick={toggleScraper} className={btnStyle("bg-blue-600")}>
          {active ? "Stop Selecting" : "Start Selecting"}
        </button>

        <button onClick={getData} className={btnStyle("bg-green-600")}>
          Get Data
        </button>

        <button onClick={clearData} className={btnStyle("bg-gray-600")}>
          Clear
        </button>
      </div>

      <div className="bg-white text-black rounded-lg p-2 max-h-48 overflow-auto shadow-inner">
        {data.length === 0 ? (
          <p className="text-center text-gray-500">No data scraped yet</p>
        ) : (
          <ul className="text-sm space-y-1">
            {data.map((d, i) => (
              <li
                key={i}
                className={`p-1 rounded ${
                  i % 2 === 0 ? "bg-gray-100" : "bg-gray-200"
                }`}
              >
                <b>{d.tag}</b>: {d.text}
              </li>
            ))}
          </ul>
        )}
      </div>

      <button
        onClick={exportCSV}
        disabled={data.length === 0}
        className={`w-full mt-3 ${btnStyle("bg-orange-500")} ${
          data.length === 0 ? "opacity-50 cursor-not-allowed" : ""
        }`}
      >
        ðŸ“¥ Export CSV
      </button>
    </div>
  );
}

export default App;
