import React, { useEffect, useState } from "react";
import "./styles/popup.css";

function App() {
  const [scrapedData, setScrapedData] = useState([]);
  const [status, setStatus] = useState("inactive");
  const [joke, setJoke] = useState(""); // API joke

  // Listen for scraped data from content.js
  useEffect(() => {
    chrome.runtime.onMessage.addListener((message) => {
      if (message.type === "SCRAPED_DATA") {
        setScrapedData(message.payload);
        setStatus("active");
      }
    });
  }, []);

  // Fetch a random joke when popup loads
  useEffect(() => {
    fetch("https://geek-jokes.sameerkumar.website/api?format=json")
      .then((res) => res.json())
      .then((data) => setJoke(data.joke)) // âœ… API returns `joke`
      .catch((err) => console.error("Error fetching joke:", err));
  }, []);

  return (
    <div className="popup-container">
      <h2 className="popup-title">ðŸ“Œ Scraped Data</h2>

      <div className="scraped-box">
        {scrapedData.length === 0 ? (
          <p className="empty-text">No data scraped yet...</p>
        ) : (
          <ul className="scraped-list">
            {scrapedData.map((item, index) => (
              <li key={index} className="scraped-item">{item}</li>
            ))}
          </ul>
        )}
      </div>

      {/* API Fun Section */}
      <div className="api-section">
        <h3>ðŸ’¡ Fun Fact</h3>
        <p>{joke || "Loading joke..."}</p>
      </div>
    </div>
  );
}

export default App;
