/* global chrome */
import { useState } from "react";

function App() {
  const [data, setData] = useState([]);
  const [active, setActive] = useState(false);

  // Toggle scraper mode
  const toggleScraper = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        { action: "toggleScraper" },
        (response) => {
          if (response) setActive(response.active);
        }
      );
    });
  };

  // Fetch scraped data
  const getData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getData" }, (response) => {
        if (response) setData(response);
      });
    });
  };

  // Clear scraped data
  const clearData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "clearData" }, () => {
        setData([]);
      });
    });
  };

  // Export CSV file
  const exportCSV = () => {
    if (data.length === 0) return;

    const csvContent =
      "data:text/csv;charset=utf-8," +
      ["Tag,Text", ...data.map((d) => `${d.tag},"${d.text}"`)].join("\n");

    const link = document.createElement("a");
    link.href = encodeURI(csvContent);
    link.download = "scraped_data.csv";
    link.click();
  };

  // Button style helper
  const btnStyle = (color) =>
    `px-3 py-1 rounded shadow text-white hover:opacity-90 transition ${color}`;

  return (
    <div className="p-4 w-72 bg-white rounded-xl shadow-md">
      <h1 className="text-lg font-bold mb-2 text-center text-blue-700">
        Data Scraper
      </h1>

      <div className="text-center mb-3">
        <span
          className={`px-2 py-1 text-xs rounded ${
            active ? "bg-green-200 text-green-800" : "bg-red-200 text-red-800"
          }`}
        >
          {active ? "ðŸŸ¢ Selecting Mode" : "ðŸ”´ Inactive"}
        </span>
      </div>

      <div className="flex flex-wrap gap-2 justify-center">
        <button onClick={toggleScraper} className={btnStyle("bg-blue-500")}>
          {active ? "Stop Selecting" : "Start Selecting"}
        </button>

        <button onClick={getData} className={btnStyle("bg-green-500")}>
          Get Data
        </button>

        <button onClick={clearData} className={btnStyle("bg-gray-500")}>
          Clear
        </button>
      </div>

      <ul className="mt-3 max-h-40 overflow-auto border rounded p-2 text-sm bg-gray-50">
        {data.length === 0 ? (
          <li className="text-gray-500 text-center">No data scraped yet</li>
        ) : (
          data.map((d, i) => (
            <li
              key={i}
              className={`p-1 rounded ${
                i % 2 === 0 ? "bg-gray-100" : "bg-gray-200"
              }`}
            >
              <b>{d.tag}</b>: {d.text}
            </li>
          ))
        )}
      </ul>

      <button
        onClick={exportCSV}
        disabled={data.length === 0}
        className={`w-full mt-3 ${btnStyle("bg-orange-500")} ${
          data.length === 0 ? "opacity-50 cursor-not-allowed" : ""
        }`}
      >
        Export CSV
      </button>
    </div>
  );
}

export default App;
