import { useState } from "react";

function App() {
  const [data, setData] = useState([]);
  const [active, setActive] = useState(false);

  const toggleScraper = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        { action: "toggleScraper" },
        (response) => setActive(response.active)
      );
    });
  };

  const getData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getData" }, (response) => {
        setData(response || []);
      });
    });
  };

  const clearData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "clearData" }, () => {
        setData([]);
      });
    });
  };

  const exportCSV = () => {
    if (data.length === 0) return;

    const csvContent =
      "data:text/csv;charset=utf-8," +
      ["Tag,Text", ...data.map((d) => `${d.tag},"${d.text}"`)].join("\n");

    const link = document.createElement("a");
    link.href = encodeURI(csvContent);
    link.download = "scraped_data.csv";
    link.click();
  };

  return (
    <div className="p-4 w-64">
      <h1 className="text-lg font-bold mb-2">Data Scraper</h1>

      <button
        onClick={toggleScraper}
        className="bg-blue-500 text-white px-3 py-1 rounded mb-2"
      >
        {active ? "Stop Selecting" : "Start Selecting"}
      </button>

      <button
        onClick={getData}
        className="bg-green-500 text-white px-3 py-1 rounded mb-2 ml-2"
      >
        Get Data
      </button>

      <button
        onClick={clearData}
        className="bg-gray-500 text-white px-3 py-1 rounded mb-2 ml-2"
      >
        Clear
      </button>

      <ul className="mt-2 max-h-32 overflow-auto border p-1">
        {data.map((d, i) => (
          <li key={i}>
            <b>{d.tag}</b>: {d.text}
          </li>
        ))}
      </ul>

      <button
        onClick={exportCSV}
        className="bg-orange-500 text-white px-3 py-1 rounded mt-2"
      >
        Export CSV
      </button>
    </div>
  );
}

export default App;
