/* global chrome */
import { useState, useEffect } from "react";
import "./styles/popup.css";   // <- CSS file
import { fetchAdvice } from "./api"; // import API function

function App() {
  const [data, setData] = useState([]);
  const [active, setActive] = useState(false);
  const [advice, setAdvice] = useState(""); // <-- new state for API data

  // Sync status on popup open
  useEffect(() => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getStatus" }, (res) => {
        if (res) setActive(res.active);
      });
    });
  }, []);

  const toggleScraper = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(
        tabs[0].id,
        { action: "toggleScraper" },
        (response) => response && setActive(response.active)
      );
    });
  };

  const getData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "getData" }, (response) => {
        if (response) setData(response);
      });
    });
  };

  const clearData = () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      chrome.tabs.sendMessage(tabs[0].id, { action: "clearData" }, () => {
        setData([]);
      });
    });
  };

  const exportCSV = () => {
    if (!data.length) return;
    const csvContent =
      "data:text/csv;charset=utf-8," +
      ["Tag,Text", ...data.map((d) => `${d.tag},"${d.text.replace(/"/g, '""')}"`)].join("\n");
    const link = document.createElement("a");
    link.href = encodeURI(csvContent);
    link.download = "scraped_data.csv";
    link.click();
  };

  // ðŸŒ Fetch Advice API
  const getAdvice = async () => {
    const newAdvice = await fetchAdvice();
    setAdvice(newAdvice);
  };

  return (
    <div className="ds-popup">
      <h1 className="ds-title">
        <span className="ds-emoji">âš¡</span>Data Scraper
      </h1>

      <div style={{ textAlign: "center", marginBottom: 12 }}>
        <span className={`ds-badge ${active ? "green" : ""}`}>
          {active ? "ðŸŸ¢ Selecting Mode" : "ðŸ”´ Inactive"}
        </span>
      </div>

      {/* Scraper Controls */}
      <div className="ds-row">
        <button onClick={toggleScraper} className="ds-btn ds-primary">
          {active ? "Stop Selecting" : "Start Selecting"}
        </button>
        <button onClick={getData} className="ds-btn ds-success">Get Data</button>
        <button onClick={clearData} className="ds-btn ds-dark">Clear</button>
      </div>

      {/* Scraped Data */}
      <div className="ds-card">
        {data.length === 0 ? (
          <p className="ds-empty">No data scraped yet</p>
        ) : (
          <ul className="ds-list">
            {data.map((d, i) => (
              <li key={i} className="ds-item">
                <b>{d.tag}</b>: {d.text}
              </li>
            ))}
          </ul>
        )}
      </div>

      <button
        onClick={exportCSV}
        disabled={data.length === 0}
        className="ds-btn ds-warn ds-export"
      >
         <img src="icons/download.png" alt="Export" className="ds-icon" />
         Export CSV
      </button>

      {/* Advice API Section */}
      <div className="ds-card advice-box" style={{ marginTop: "20px" }}>
        <h2>ðŸ’¡ Random Advice</h2>
        <button onClick={getAdvice} className="ds-btn ds-primary">
          Get Advice
        </button>
        <p style={{ marginTop: "10px" }}>{advice}</p>
      </div>
    </div>
  );
}

export default App;
